import React from 'react'
import './PokemonList.css'

class ListItem extends React.Component {
    constructor(){
        super(props);
    }

    handleClick (event) {
        props.handlePokemonClick(event)
    }  

    render() {
    return (
        <>
            {this.props.allPokemon.map((pokemon) => {
                return <li value={'geraffe'} onClick={this.handleClick.bind(this)}>{pokemon.name}</li>
            })}
        </>
    )
}
}

export default ListItem
