import React from 'react'
import './MyPokemon.css'

const MyPokemon = (props) => {
    return (
        <div className="my-pokemon-container">
            <h2>My Pokemon</h2>
    {props.favouritePokemon && props.favouritePokemon.map(pokemon => {
    return(
    <>
    <li onClick={() => props.handlePokemonClick(pokemon.name)}>{pokemon.name}</li><button onClick={() => props.deleteFavourite(pokemon._id)}>Delete</button>
    </>
    )
    })}
        </div>
    )
}

export default MyPokemon
